UPDATE `civicrm_msg_template`
SET `msg_title` = 'Events - Registration Confirmation and Receipt (off-line)',
  `msg_subject` = '{ts}Event Confirmation{/ts} - {$event.title}',
  `msg_text` = '{if $event.confirm_email_text AND (not $isOnWaitlist AND not $isRequireApproval)}\r\n{$event.confirm_email_text}\r\n{/if}\r\n\r\n{if $isOnWaitlist}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{ts}You have been added to the WAIT LIST for this event.{/ts}\r\n\r\n{if $isPrimary}\r\n{ts}If space becomes available you will receive an email with\r\na link to a web page where you can complete your registration.{/ts}\r\n\r\n{/if}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{elseif $isRequireApproval}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{ts}Your registration has been submitted.{/ts}\r\n\r\n{if $isPrimary}\r\n{ts}Once your registration has been reviewed, you will receive\r\nan email with a link to a web page where you can complete the\r\nregistration process.{/ts}\r\n\r\n{/if}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{elseif $is_pay_later}\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$pay_later_receipt}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{else}\r\n\r\n{/if}\r\n\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{ts}Event Information and Location{/ts}\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$event.event_title}\r\n{$event.event_start_date|crmDate}{if $event.event_end_date}-{if $event.event_end_date|date_format:"%Y%m%d" == $event.event_start_date|date_format:"%Y%m%d"}{$event.event_end_date|crmDate:0:1}{else}{$event.event_end_date|crmDate}{/if}{/if}\r\n\r\n{if $event.participant_role neq ''Attendee'' and $defaultRole}\r\n{ts}Participant Role{/ts}: {$event.participant_role}\r\n{/if}\r\n\r\n{if $isShowLocation}\r\n{if $location.address.1.name}\r\n\r\n{$location.address.1.name}\r\n{/if}\r\n{if $location.address.1.street_address}{$location.address.1.street_address}\r\n{/if}\r\n{if $location.address.1.supplemental_address_1}{$location.address.1.supplemental_address_1}\r\n{/if}\r\n{if $location.address.1.supplemental_address_2}{$location.address.1.supplemental_address_2}\r\n{/if}\r\n{if $location.address.1.city}{$location.address.1.city} {$location.address.1.postal_code}{if $location.address.1.postal_code_suffix} - {$location.address.1.postal_code_suffix}{/if}\r\n{/if}\r\n\r\n{/if}{*End of isShowLocation condition*}\r\n\r\n{if $location.phone.1.phone || $location.email.1.email}\r\n\r\n{ts}Event Contacts:{/ts}\r\n{foreach from=$location.phone item=phone}\r\n{if $phone.phone}\r\n\r\n{if $phone.phone_type}{$phone.phone_type_display}{else}{ts}Phone{/ts}{/if}: {$phone.phone}{/if} {if $phone.phone_ext} {ts}ext.{/ts} {$phone.phone_ext}{/if}\r\n{/foreach}\r\n{foreach from=$location.email item=eventEmail}\r\n{if $eventEmail.email}\r\n\r\n{ts}Email{/ts}: {$eventEmail.email}{/if}{/foreach}\r\n{/if}\r\n\r\n{if $email}\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{ts}Registered Email{/ts}\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$email}\r\n{/if}\r\n{if $event.is_monetary} {* This section for Paid events only.*}\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$event.fee_label}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{if $lineItem}{foreach from=$lineItem item=value key=priceset}\r\n\r\n{if $value neq ''skip''}\r\n{if $isPrimary}\r\n{if $lineItem|@count GT 1} {* Header for multi participant registration cases. *}\r\n{ts 1=$priceset+1}Participant %1{/ts}\r\n{/if}\r\n{/if}\r\n---------------------------------------------------------{if $pricesetFieldsCount }--------------------{/if}\r\n\r\n{capture assign=ts_item}{ts}Item{/ts}{/capture}\r\n{capture assign=ts_qty}{ts}Qty{/ts}{/capture}\r\n{capture assign=ts_each}{ts}Each{/ts}{/capture}\r\n{capture assign=ts_total}{ts}Total{/ts}{/capture}\r\n{capture assign=ts_participant_total}{if $pricesetFieldsCount }{ts}Total Participants{/ts}{/if}{/capture}\r\n{$ts_item|string_format:"%-30s"} {$ts_qty|string_format:"%5s"} {$ts_each|string_format:"%10s"} {$ts_total|string_format:"%10s"} {$ts_participant_total|string_format:"%10s"}\r\n----------------------------------------------------------{if $pricesetFieldsCount }--------------------{/if}\r\n\r\n{foreach from=$value item=line}\r\n{if $pricesetFieldsCount }{capture assign=ts_participant_count}{$line.participant_count}{/capture}{/if}\r\n{capture assign=ts_item}{if $line.html_type eq ''Text''}{$line.label}{else}{$line.field_title} - {$line.label}{/if} {if $line.description} {$line.description}{/if}{/capture}{$ts_item|truncate:30:"..."|string_format:"%-30s"} {$line.qty|string_format:"%5s"} {$line.unit_price|crmMoney|string_format:"%10s"} {$line.line_total|crmMoney|string_format:"%10s"} {$ts_participant_count|string_format:"%10s"}\r\n{/foreach}\r\n{/if}\r\n{/foreach}\r\n{/if}\r\n{if $amount && !$lineItem}\r\n{foreach from=$amount item=amnt key=level}{$amnt.amount|crmMoney} {$amnt.label}\r\n{/foreach}\r\n{/if}\r\n{if $isPrimary }\r\n\r\n{ts}Total Amount{/ts}: {$totalAmount|crmMoney} {if $hookDiscount.message}({$hookDiscount.message}){/if}\r\n\r\n{if $pricesetFieldsCount }\r\n      {assign var="count" value= 0}\r\n      {foreach from=$lineItem item=pcount}\r\n      {assign var="lineItemCount" value=0}\r\n      {if $pcount neq ''skip''}\r\n        {foreach from=$pcount item=p_count}\r\n        {assign var="lineItemCount" value=$lineItemCount+$p_count.participant_count}\r\n        {/foreach}\r\n        {if $lineItemCount < 1 }\r\n        {assign var="lineItemCount" value=1}\r\n        {/if}	\r\n      {assign var="count" value=$count+$lineItemCount}\r\n      {/if}\r\n      {/foreach}\r\n\r\n{ts}Total Participants{/ts}: {$count}\r\n{/if}\r\n   \r\n{if $is_pay_later }\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$pay_later_receipt}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{/if}\r\n\r\n{if $register_date}\r\n{ts}Registration Date{/ts}: {$register_date|crmDate}\r\n{/if}\r\n{if $receive_date}\r\n{ts}Transaction Date{/ts}: {$receive_date|crmDate}\r\n{/if}\r\n{if $contributionTypeName}\r\n{ts}Financial Type{/ts}: {$contributionTypeName}\r\n{/if}\r\n{if $trxn_id}\r\n{ts}Transaction #{/ts}: {$trxn_id}\r\n{/if}\r\n{if $paidBy}\r\n{ts}Paid By{/ts}: {$paidBy}\r\n{/if}\r\n{if $checkNumber}\r\n{ts}Check Number{/ts}: {$checkNumber}\r\n{/if}\r\n{if $contributeMode ne ''notify'' and !$isAmountzero and !$is_pay_later and !$isOnWaitlist and !$isRequireApproval}\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{ts}Billing Name and Address{/ts}\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$billingName}\r\n{$address}\r\n{/if}\r\n\r\n{if $contributeMode eq ''direct'' and !$isAmountzero and !$is_pay_later and !$isOnWaitlist and !$isRequireApproval}\r\n===========================================================\r\n{ts}Credit Card Information{/ts}\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$credit_card_type}\r\n{$credit_card_number}\r\n{ts}Expires{/ts}: {$credit_card_exp_date|truncate:7:''''|crmDate}\r\n{/if}\r\n{/if}\r\n{/if} {* End of conditional section for Paid events *}\r\n\r\n{if $customPre}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$customPre_grouptitle}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{foreach from=$customPre item=value key=customName}\r\n{if ( $trackingFields and ! in_array( $customName, $trackingFields ) ) or ! $trackingFields}\r\n{$customName}: {$value}\r\n{/if}\r\n{/foreach}\r\n{/if}\r\n\r\n{if $customPost}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$customPost_grouptitle}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{foreach from=$customPost item=value key=customName}\r\n{if ( $trackingFields and ! in_array( $customName, $trackingFields ) ) or ! $trackingFields}\r\n{$customName}: {$value}\r\n{/if}\r\n{/foreach}\r\n{/if}\r\n{if $customProfile}\r\n\r\n{foreach from=$customProfile item=value key=customName}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{ts 1=$customName+1}Participant Information - Participant %1{/ts}\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{foreach from=$value item=val key=field}\r\n{if $field eq ''additionalCustomPre'' or $field eq ''additionalCustomPost'' }\r\n{if $field eq ''additionalCustomPre'' }\r\n----------------------------------------------------------{if $pricesetFieldsCount }--------------------{/if}\r\n\r\n{$additionalCustomPre_grouptitle}\r\n----------------------------------------------------------{if $pricesetFieldsCount }--------------------{/if}\r\n\r\n{else}\r\n----------------------------------------------------------{if $pricesetFieldsCount }--------------------{/if}\r\n\r\n{$additionalCustomPost_grouptitle}\r\n----------------------------------------------------------{if $pricesetFieldsCount }--------------------{/if}\r\n\r\n{/if}\r\n{foreach from=$val item=v key=f}\r\n{$f}: {$v}\r\n{/foreach}\r\n{/if}\r\n{/foreach}\r\n{/foreach}\r\n{/if}\r\n{if $customGroup}\r\n{foreach from=$customGroup item=value key=customName}\r\n=========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$customName}\r\n=========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{foreach from=$value item=v key=n}\r\n{$n}: {$v}\r\n{/foreach}\r\n{/foreach}\r\n{/if}',
  `msg_html` = '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\r\n<html xmlns="http://www.w3.org/1999/xhtml">\r\n<head>\r\n <meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />\r\n <title></title>\r\n</head>\r\n<body>\r\n\r\n{capture assign=headerStyle}colspan="2" style="text-align: left; padding: 4px; border-bottom: 1px solid #999; background-color: #eee;"{/capture}\r\n{capture assign=labelStyle }style="padding: 4px; border-bottom: 1px solid #999; background-color: #f7f7f7;"{/capture}\r\n{capture assign=valueStyle }style="padding: 4px; border-bottom: 1px solid #999;"{/capture}\r\n\r\n<center>\r\n <table width="620" border="0" cellpadding="0" cellspacing="0" id="crm-event_receipt" style="font-family: Arial, Verdana, sans-serif; text-align: left;">\r\n\r\n  <!-- BEGIN HEADER -->\r\n  <!-- You can add table row(s) here with logo or other header elements -->\r\n  <!-- END HEADER -->\r\n\r\n  <!-- BEGIN CONTENT -->\r\n\r\n  <tr>\r\n   <td>\r\n\r\n    {if $event.confirm_email_text AND (not $isOnWaitlist AND not $isRequireApproval)}\r\n     <p>{$event.confirm_email_text|htmlize}</p>\r\n    {/if}\r\n\r\n    {if $isOnWaitlist}\r\n     <p>{ts}You have been added to the WAIT LIST for this event.{/ts}</p>\r\n     {if $isPrimary}\r\n       <p>{ts}If space becomes available you will receive an email with\r\na link to a web page where you can complete your registration.{/ts}</p>\r\n     {/if}\r\n    {elseif $isRequireApproval}\r\n     <p>{ts}Your registration has been submitted.{/ts}</p>\r\n     {if $isPrimary}\r\n      <p>{ts}Once your registration has been reviewed, you will receive\r\nan email with a link to a web page where you can complete the\r\nregistration process.{/ts}</p>\r\n     {/if}\r\n    {elseif $is_pay_later}\r\n     <p>{$pay_later_receipt}</p> {* FIXME: this might be text rather than HTML *}\r\n    {/if}\r\n\r\n   </td>\r\n  </tr>\r\n  <tr>\r\n   <td>\r\n    <table style="border: 1px solid #999; margin: 1em 0em 1em; border-collapse: collapse; width:100%;">\r\n     <tr>\r\n      <th {$headerStyle}>\r\n       {ts}Event Information and Location{/ts}\r\n      </th>\r\n     </tr>\r\n     <tr>\r\n      <td colspan="2" {$valueStyle}>\r\n       {$event.event_title}<br />\r\n       {$event.event_start_date|crmDate}{if $event.event_end_date}-{if $event.event_end_date|date_format:"%Y%m%d" == $event.event_start_date|date_format:"%Y%m%d"}{$event.event_end_date|crmDate:0:1}{else}{$event.event_end_date|crmDate}{/if}{/if}\r\n      </td>\r\n     </tr>\r\n\r\n     {if $event.participant_role neq ''Attendee'' and $defaultRole}\r\n      <tr>\r\n       <td {$labelStyle}>\r\n        {ts}Participant Role{/ts}\r\n       </td>\r\n       <td {$valueStyle}>\r\n        {$event.participant_role}\r\n       </td>\r\n      </tr>\r\n     {/if}\r\n\r\n     {if $isShowLocation}\r\n      <tr>\r\n       <td colspan="2" {$valueStyle}>\r\n        {if $location.address.1.name}\r\n         {$location.address.1.name}<br />\r\n        {/if}\r\n        {if $location.address.1.street_address}\r\n         {$location.address.1.street_address}<br />\r\n        {/if}\r\n        {if $location.address.1.supplemental_address_1}\r\n         {$location.address.1.supplemental_address_1}<br />\r\n        {/if}\r\n        {if $location.address.1.supplemental_address_2}\r\n         {$location.address.1.supplemental_address_2}<br />\r\n        {/if}\r\n        {if $location.address.1.city}\r\n         {$location.address.1.city} {$location.address.1.postal_code}{if $location.address.1.postal_code_suffix} - {$location.address.1.postal_code_suffix}{/if}<br />\r\n        {/if}\r\n       </td>\r\n      </tr>\r\n     {/if}\r\n\r\n     {if $location.phone.1.phone || $location.email.1.email}\r\n      <tr>\r\n       <td colspan="2" {$labelStyle}>\r\n        {ts}Event Contacts:{/ts}\r\n       </td>\r\n      </tr>\r\n      {foreach from=$location.phone item=phone}\r\n       {if $phone.phone}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {if $phone.phone_type}\r\n           {$phone.phone_type_display}\r\n          {else}\r\n           {ts}Phone{/ts}\r\n          {/if}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$phone.phone} {if $phone.phone_ext}&nbsp;{ts}ext.{/ts} {$phone.phone_ext}{/if}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n      {/foreach}\r\n      {foreach from=$location.email item=eventEmail}\r\n       {if $eventEmail.email}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {ts}Email{/ts}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$eventEmail.email}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n      {/foreach}\r\n     {/if}\r\n     <tr>\r\n      <td colspan="2" {$valueStyle}>\r\n      </td>\r\n     </tr>\r\n     {if $email}\r\n      <tr>\r\n       <th {$headerStyle}>\r\n        {ts}Registered Email{/ts}\r\n       </th>\r\n      </tr>\r\n      <tr>\r\n       <td colspan="2" {$valueStyle}>\r\n        {$email}\r\n       </td>\r\n      </tr>\r\n     {/if}\r\n\r\n\r\n     {if $event.is_monetary}\r\n\r\n      <tr>\r\n       <th {$headerStyle}>\r\n        {$event.fee_label}\r\n       </th>\r\n      </tr>\r\n\r\n      {if $lineItem}\r\n       {foreach from=$lineItem item=value key=priceset}\r\n        {if $value neq ''skip''}\r\n         {if $isPrimary}\r\n          {if $lineItem|@count GT 1} {* Header for multi participant registration cases. *}\r\n           <tr>\r\n            <td colspan="2" {$labelStyle}>\r\n             {ts 1=$priceset+1}Participant %1{/ts}\r\n            </td>\r\n           </tr>\r\n          {/if}\r\n         {/if}\r\n         <tr>\r\n          <td colspan="2" {$valueStyle}>\r\n           <table> {* FIXME: style this table so that it looks like the text version (justification, etc.) *}\r\n            <tr>\r\n             <th>{ts}Item{/ts}</th>\r\n             <th>{ts}Qty{/ts}</th>\r\n             <th>{ts}Each{/ts}</th>\r\n             <th>{ts}Total{/ts}</th>\r\n	     {if $pricesetFieldsCount }<th>{ts}Total Participants{/ts}</th>{/if}\r\n            </tr>\r\n            {foreach from=$value item=line}\r\n             <tr>\r\n              <td>\r\n	      {if $line.html_type eq ''Text''}{$line.label}{else}{$line.field_title} - {$line.label}{/if} {if $line.description}<div>{$line.description|truncate:30:"..."}</div>{/if}	\r\n              </td>\r\n              <td>\r\n               {$line.qty}\r\n              </td>\r\n              <td>\r\n               {$line.unit_price|crmMoney}\r\n              </td>\r\n              <td>\r\n               {$line.line_total|crmMoney}\r\n              </td>\r\n	      {if  $pricesetFieldsCount }\r\n	      <td>\r\n		{$line.participant_count}\r\n              </td>\r\n	      {/if}\r\n             </tr>\r\n            {/foreach}\r\n           </table>\r\n          </td>\r\n         </tr>\r\n        {/if}\r\n       {/foreach}\r\n      {/if}\r\n\r\n      {if $amount && !$lineItem}\r\n       {foreach from=$amount item=amnt key=level}\r\n        <tr>\r\n         <td colspan="2" {$valueStyle}>\r\n          {$amnt.amount|crmMoney} {$amnt.label}\r\n         </td>\r\n        </tr>\r\n       {/foreach}\r\n      {/if}\r\n      {if $isPrimary}\r\n       <tr>\r\n        <td {$labelStyle}>\r\n         {ts}Total Amount{/ts}\r\n        </td>\r\n        <td {$valueStyle}>\r\n         {$totalAmount|crmMoney} {if $hookDiscount.message}({$hookDiscount.message}){/if}\r\n        </td>\r\n       </tr>\r\n       {if $pricesetFieldsCount }\r\n     <tr>\r\n       <td {$labelStyle}> \r\n	 {ts}Total Participants{/ts}</td>   \r\n       <td {$valueStyle}>\r\n	 {assign var="count" value= 0}\r\n         {foreach from=$lineItem item=pcount}\r\n         {assign var="lineItemCount" value=0}\r\n         {if $pcount neq ''skip''}\r\n           {foreach from=$pcount item=p_count}\r\n           {assign var="lineItemCount" value=$lineItemCount+$p_count.participant_count}\r\n           {/foreach}\r\n           {if $lineItemCount < 1 }\r\n           assign var="lineItemCount" value=1}\r\n           {/if}	\r\n           {assign var="count" value=$count+$lineItemCount}\r\n         {/if}\r\n         {/foreach}\r\n	 {$count}\r\n       </td>\r\n     </tr>\r\n     {/if}\r\n       {if $is_pay_later}\r\n        <tr>\r\n         <td colspan="2" {$labelStyle}>\r\n          {$pay_later_receipt}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n\r\n       {if $register_date}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {ts}Registration Date{/ts}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$register_date|crmDate}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n\r\n       {if $receive_date}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {ts}Transaction Date{/ts}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$receive_date|crmDate}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n\r\n       {if $contributionTypeName}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {ts}Financial Type{/ts}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$contributionTypeName}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n\r\n       {if $trxn_id}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {ts}Transaction #{/ts}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$trxn_id}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n\r\n       {if $paidBy}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {ts}Paid By{/ts}\r\n         </td>\r\n         <td {$valueStyle}>\r\n         {$paidBy}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n\r\n       {if $checkNumber}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {ts}Check Number{/ts}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$checkNumber}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n\r\n       {if $contributeMode ne ''notify'' and !$isAmountzero and !$is_pay_later and !$isOnWaitlist and !$isRequireApproval}\r\n        <tr>\r\n         <th {$headerStyle}>\r\n          {ts}Billing Name and Address{/ts}\r\n         </th>\r\n        </tr>\r\n        <tr>\r\n         <td colspan="2" {$valueStyle}>\r\n          {$billingName}<br />\r\n          {$address|nl2br}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n\r\n       {if $contributeMode eq ''direct'' and !$isAmountzero and !$is_pay_later and !$isOnWaitlist and !$isRequireApproval}\r\n        <tr>\r\n         <th {$headerStyle}>\r\n          {ts}Credit Card Information{/ts}\r\n         </th>\r\n        </tr>\r\n        <tr>\r\n         <td colspan="2" {$valueStyle}>\r\n          {$credit_card_type}<br />\r\n          {$credit_card_number}<br />\r\n          {ts}Expires{/ts}: {$credit_card_exp_date|truncate:7:''''|crmDate}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n\r\n      {/if}\r\n\r\n     {/if} {* End of conditional section for Paid events *}\r\n\r\n     {if $customPre}\r\n      <tr>\r\n       <th {$headerStyle}>\r\n        {$customPre_grouptitle}\r\n       </th>\r\n      </tr>\r\n      {foreach from=$customPre item=value key=customName}\r\n       {if ( $trackingFields and ! in_array( $customName, $trackingFields ) ) or ! $trackingFields}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {$customName}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$value}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n      {/foreach}\r\n     {/if}\r\n\r\n     {if $customPost}\r\n      <tr>\r\n       <th {$headerStyle}>\r\n        {$customPost_grouptitle}\r\n       </th>\r\n      </tr>\r\n      {foreach from=$customPost item=value key=customName}\r\n       {if ( $trackingFields and ! in_array( $customName, $trackingFields ) ) or ! $trackingFields}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {$customName}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$value}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n      {/foreach}\r\n     {/if}\r\n\r\n     {if $customProfile}\r\n      {foreach from=$customProfile item=value key=customName}\r\n       <tr>\r\n        <th {$headerStyle}>\r\n         {ts 1=$customName+1}Participant Information - Participant %1{/ts}\r\n        </th>\r\n       </tr>\r\n       {foreach from=$value item=val key=field}\r\n        {if $field eq ''additionalCustomPre'' or $field eq ''additionalCustomPost''}\r\n         <tr>\r\n          <td colspan="2" {$labelStyle}>\r\n           {if $field eq ''additionalCustomPre''}\r\n            {$additionalCustomPre_grouptitle}\r\n           {else}\r\n            {$additionalCustomPost_grouptitle}\r\n           {/if}\r\n          </td>\r\n         </tr>\r\n         {foreach from=$val item=v key=f}\r\n          <tr>\r\n           <td {$labelStyle}>\r\n            {$f}\r\n           </td>\r\n           <td {$valueStyle}>\r\n            {$v}\r\n           </td>\r\n          </tr>\r\n         {/foreach}\r\n        {/if}\r\n       {/foreach}\r\n      {/foreach}\r\n     {/if}\r\n\r\n     {if $customGroup}\r\n      {foreach from=$customGroup item=value key=customName}\r\n       <tr>\r\n        <th {$headerStyle}>\r\n         {$customName}\r\n        </th>\r\n       </tr>\r\n       {foreach from=$value item=v key=n}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {$n}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$v}\r\n         </td>\r\n        </tr>\r\n       {/foreach}\r\n      {/foreach}\r\n     {/if}\r\n\r\n    </table>\r\n   </td>\r\n  </tr>\r\n\r\n </table>\r\n</center>\r\n\r\n</body>\r\n</html>',
  `is_active` = 1,
  `workflow_id` = 501,
  `is_default` = 1,
  `is_reserved` = 0,
  `pdf_format_id` = NULL
WHERE `id` = 17;

UPDATE `civicrm_msg_template`
SET `msg_title` = 'Events - Registration Confirmation and Receipt (on-line)',
  `msg_subject` = '{if $isOnWaitlist}{ts}Wait List Confirmation{/ts}{else}{ts}Registration Confirmation{/ts}{/if} - {$title}',
  `msg_text` = 'Dear {contact.display_name},\r\n\r\n{if $event.confirm_email_text AND (not $isOnWaitlist AND not $isRequireApproval)}\r\n{$event.confirm_email_text}\r\n\r\n{else}\r\nThank you for your participation.  This letter is a confirmation that your registration has been received and your status has been updated to {if $participant_status}$participant_status{else}{if $isOnWaitlist}waitlisted{else}registered{/if}{/if} for the following:\r\n\r\n{/if}\r\n\r\n{if $isOnWaitlist}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{ts}You have been added to the WAIT LIST for this event.{/ts}\r\n\r\n{if $isPrimary}\r\n{ts}If space becomes available you will receive an email with\r\na link to a web page where you can complete your registration.{/ts}\r\n{/if}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{elseif $isRequireApproval}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{ts}Your registration has been submitted.{/ts}\r\n\r\n{if $isPrimary}\r\n{ts}Once your registration has been reviewed, you will receive\r\nan email with a link to a web page where you can complete the\r\nregistration process.{/ts}\r\n\r\n{/if}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{elseif $is_pay_later && !$isAmountzero}\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$pay_later_receipt}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{else}\r\n\r\n{/if}\r\n\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{ts}Event Information and Location{/ts}\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$event.event_title}\r\n{$event.event_start_date|date_format:"%A"} {$event.event_start_date|crmDate}{if $event.event_end_date}-{if $event.event_end_date|date_format:"%Y%m%d" == $event.event_start_date|date_format:"%Y%m%d"}{$event.event_end_date|crmDate:0:1}{else}{$event.event_end_date|date_format:"%A"} {$event.event_end_date|crmDate}{/if}{/if}\r\n{if $conference_sessions}\r\n\r\n\r\n{ts}Your schedule:{/ts}\r\n{assign var=''group_by_day'' value=''NA''}\r\n{foreach from=$conference_sessions item=session}\r\n{if $session.start_date|date_format:"%Y/%m/%d" != $group_by_day|date_format:"%Y/%m/%d"}\r\n{assign var=''group_by_day'' value=$session.start_date}\r\n\r\n{$group_by_day|date_format:"%m/%d/%Y"}\r\n\r\n\r\n{/if}\r\n{$session.start_date|crmDate:0:1}{if $session.end_date}-{$session.end_date|crmDate:0:1}{/if} {$session.title}\r\n{if $session.location}    {$session.location}{/if}\r\n{/foreach}\r\n{/if}\r\n\r\n{if $event.participant_role neq ''Attendee'' and $defaultRole}\r\n{ts}Participant Role{/ts}: {$event.participant_role}\r\n{/if}\r\n\r\n{if $isShowLocation}\r\n{if $location.address.1.name}\r\n\r\n{$location.address.1.name}\r\n{/if}\r\n{if $location.address.1.street_address}{$location.address.1.street_address}\r\n{/if}\r\n{if $location.address.1.supplemental_address_1}{$location.address.1.supplemental_address_1}\r\n{/if}\r\n{if $location.address.1.supplemental_address_2}{$location.address.1.supplemental_address_2}\r\n{/if}\r\n{if $location.address.1.city}{$location.address.1.city}, {$location.address.1.state_province} {$location.address.1.postal_code}{if $location.address.1.postal_code_suffix} - {$location.address.1.postal_code_suffix}{/if}\r\n{/if}\r\n\r\n{/if}{*End of isShowLocation condition*}\r\n\r\n{if $location.phone.1.phone || $location.email.1.email}\r\n\r\n{ts}Event Contacts:{/ts}\r\n{foreach from=$location.phone item=phone}\r\n{if $phone.phone}\r\n\r\n{if $phone.phone_type}{$phone.phone_type_display}{else}{ts}Phone{/ts}{/if}: {$phone.phone}{/if} {if $phone.phone_ext} {ts}ext.{/ts} {$phone.phone_ext}{/if}\r\n{/foreach}\r\n{foreach from=$location.email item=eventEmail}\r\n{if $eventEmail.email}\r\n\r\n{ts}Email{/ts}: {$eventEmail.email}{/if}{/foreach}\r\n{/if}\r\n\r\n{if $payer.name}\r\nYou were registered by: {$payer.name}\r\n{/if}\r\n{if $event.is_monetary} {* This section for Paid events only.*}\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$event.fee_label}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{if $lineItem}{foreach from=$lineItem item=value key=priceset}\r\n\r\n{if $value neq ''skip''}\r\n{if $isPrimary}\r\n{if $lineItem|@count GT 1} {* Header for multi participant registration cases. *}\r\n{ts 1=$priceset+1}Participant %1{/ts} {$part.$priceset.info}\r\n\r\n{/if}\r\n{/if}\r\n-----------------------------------------------------------{if $pricesetFieldsCount }--------------------{/if}\r\n\r\n{capture assign=ts_item}{ts}Item{/ts}{/capture}\r\n{capture assign=ts_qty}{ts}Qty{/ts}{/capture}\r\n{capture assign=ts_each}{ts}Each{/ts}{/capture}\r\n{capture assign=ts_total}{ts}Total{/ts}{/capture}\r\n{if $pricesetFieldsCount }{capture assign=ts_participant_total}{ts}Total Participants{/ts}{/capture}{/if}\r\n{$ts_item|string_format:"%-30s"} {$ts_qty|string_format:"%5s"} {$ts_each|string_format:"%10s"} {$ts_total|string_format:"%10s"} {$ts_participant_total|string_format:"%10s"}\r\n-----------------------------------------------------------{if $pricesetFieldsCount }--------------------{/if}\r\n\r\n{foreach from=$value item=line}\r\n{if $pricesetFieldsCount }{capture assign=ts_participant_count}{$line.participant_count}{/capture}{/if}\r\n{capture assign=ts_item}{if $line.html_type eq ''Text''}{$line.label}{else}{$line.field_title} - {$line.label}{/if} {if $line.description} {$line.description}{/if}{/capture}{$ts_item|truncate:30:"..."|string_format:"%-30s"} {$line.qty|string_format:"%5s"} {$line.unit_price|crmMoney:$currency|string_format:"%10s"} {$line.line_total|crmMoney:$currency|string_format:"%10s"}{$ts_participant_count|string_format:"%10s"}\r\n{/foreach}\r\n{/if}\r\n{/foreach}\r\n{/if}\r\n{if $amounts && !$lineItem}\r\n{foreach from=$amounts item=amnt key=level}{$amnt.amount|crmMoney:$currency} {$amnt.label}\r\n{/foreach}\r\n{/if}\r\n{if $isPrimary }\r\n\r\n{ts}Total Amount{/ts}: {$totalAmount|crmMoney:$currency} {if $hookDiscount.message}({$hookDiscount.message}){/if}\r\n\r\n{if $pricesetFieldsCount }\r\n      {assign var="count" value= 0}\r\n      {foreach from=$lineItem item=pcount}\r\n      {assign var="lineItemCount" value=0}\r\n      {if $pcount neq ''skip''}\r\n        {foreach from=$pcount item=p_count}\r\n        {assign var="lineItemCount" value=$lineItemCount+$p_count.participant_count}\r\n        {/foreach}\r\n      {if $lineItemCount < 1 }\r\n        {assign var="lineItemCount" value=1}\r\n      {/if}	\r\n      {assign var="count" value=$count+$lineItemCount}\r\n      {/if}\r\n      {/foreach}\r\n\r\n{ts}Total Participants{/ts}: {$count}\r\n{/if}\r\n  \r\n{if $register_date}\r\n{ts}Registration Date{/ts}: {$register_date|crmDate}\r\n{/if}\r\n{if $receive_date}\r\n{ts}Transaction Date{/ts}: {$receive_date|crmDate}\r\n{/if}\r\n{if $contributionTypeName}\r\n{ts}Financial Type{/ts}: {$contributionTypeName}\r\n{/if}\r\n{if $trxn_id}\r\n{ts}Transaction #{/ts}: {$trxn_id}\r\n{/if}\r\n{if $paidBy}\r\n{ts}Paid By{/ts}: {$paidBy}\r\n{/if}\r\n{if $checkNumber}\r\n{ts}Check Number{/ts}: {$checkNumber}\r\n{/if}\r\n{if $contributeMode ne ''notify'' and !$isAmountzero and !$is_pay_later and !$isOnWaitlist and !$isRequireApproval}\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{ts}Billing Name and Address{/ts}\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$billingName}\r\n{$address}\r\n{/if}\r\n\r\n{if $contributeMode eq ''direct'' and !$isAmountzero and !$is_pay_later and !$isOnWaitlist and !$isRequireApproval}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{ts}Credit Card Information{/ts}\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$credit_card_type}\r\n{$credit_card_number}\r\n{ts}Expires{/ts}: {$credit_card_exp_date|truncate:7:''''|crmDate}\r\n{/if}\r\n{/if}\r\n{/if} {* End of conditional section for Paid events *}\r\n\r\n{if $customPre}\r\n{foreach from=$customPre item=customPr key=i}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$customPre_grouptitle.$i}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{foreach from=$customPr item=customValue key=customName}\r\n{if ( $trackingFields and ! in_array( $customName, $trackingFields ) ) or ! $trackingFields}\r\n {$customName}: {$customValue}\r\n{/if}\r\n{/foreach}\r\n{/foreach}\r\n{/if}\r\n\r\n{if $customPost}\r\n{foreach from=$customPost item=customPos key=j}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$customPost_grouptitle.$j} \r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{foreach from=$customPos item=customValue key=customName}\r\n{if ( $trackingFields and ! in_array( $customName, $trackingFields ) ) or ! $trackingFields}\r\n {$customName}: {$customValue}\r\n{/if}\r\n{/foreach}\r\n{/foreach}\r\n{/if}\r\n{if $customProfile}\r\n\r\n{foreach from=$customProfile.profile item=eachParticipant key=participantID}\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{ts 1=$participantID+2}Participant Information - Participant %1{/ts}\r\n\r\n==========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{foreach from=$eachParticipant item=eachProfile key=pid}\r\n----------------------------------------------------------{if $pricesetFieldsCount }--------------------{/if}\r\n\r\n{$customProfile.title.$pid}\r\n----------------------------------------------------------{if $pricesetFieldsCount }--------------------{/if}\r\n\r\n{foreach from=$eachProfile item=val key=field}\r\n{foreach from=$val item=v key=f}\r\n{$field}: {$v}\r\n{/foreach}\r\n{/foreach}\r\n{/foreach}\r\n{/foreach}\r\n{/if}\r\n{if $customGroup}\r\n{foreach from=$customGroup item=value key=customName}\r\n=========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{$customName}\r\n=========================================================={if $pricesetFieldsCount }===================={/if}\r\n\r\n{foreach from=$value item=v key=n}\r\n{$n}: {$v}\r\n{/foreach}\r\n{/foreach}\r\n{/if}',
  `msg_html` = '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\r\n<html xmlns="http://www.w3.org/1999/xhtml">\r\n<head>\r\n <meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />\r\n <title></title>\r\n</head>\r\n<body>\r\n\r\n{capture assign=headerStyle}colspan="2" style="text-align: left; padding: 4px; border-bottom: 1px solid #999; background-color: #eee;"{/capture}\r\n{capture assign=labelStyle }style="padding: 4px; border-bottom: 1px solid #999; background-color: #f7f7f7;"{/capture}\r\n{capture assign=valueStyle }style="padding: 4px; border-bottom: 1px solid #999;"{/capture}\r\n\r\n<center>\r\n <table width="500" border="0" cellpadding="0" cellspacing="0" id="crm-event_receipt" style="font-family: Arial, Verdana, sans-serif; text-align: left;">\r\n\r\n  <!-- BEGIN HEADER -->\r\n  <!-- You can add table row(s) here with logo or other header elements -->\r\n  <!-- END HEADER -->\r\n\r\n  <!-- BEGIN CONTENT -->\r\n\r\n  <tr>\r\n   <td>\r\n	<p>Dear {contact.display_name},</p>\r\n\r\n    {if $event.confirm_email_text AND (not $isOnWaitlist AND not $isRequireApproval)}\r\n     <p>{$event.confirm_email_text|htmlize}</p>\r\n\r\n    {else}\r\n	<p>Thank you for your participation.  This letter is a confirmation that your registration has been received and your status has been updated to <strong>{if $isOnWaitlist}waitlisted{else}registered{/if}</strong> for the following:</p>\r\n\r\n    {/if}\r\n\r\n    <p>\r\n    {if $isOnWaitlist}\r\n     <p>{ts}You have been added to the WAIT LIST for this event.{/ts}</p>\r\n     {if $isPrimary}\r\n       <p>{ts}If space becomes available you will receive an email with\r\na link to a web page where you can complete your registration.{/ts}</p>\r\n     {/if}\r\n    {elseif $isRequireApproval}\r\n     <p>{ts}Your registration has been submitted.{/ts}</p>\r\n     {if $isPrimary}\r\n      <p>{ts}Once your registration has been reviewed, you will receive\r\nan email with a link to a web page where you can complete the\r\nregistration process.{/ts}</p>\r\n     {/if}\r\n    {elseif $is_pay_later && !$isAmountzero}\r\n     <p>{$pay_later_receipt}</p> {* FIXME: this might be text rather than HTML *}\r\n    {/if}\r\n\r\n   </td>\r\n  </tr>\r\n  <tr>\r\n   <td>\r\n    <table width="500" style="border: 1px solid #999; margin: 1em 0em 1em; border-collapse: collapse;">\r\n     <tr>\r\n      <th {$headerStyle}>\r\n       {ts}Event Information and Location{/ts}\r\n      </th>\r\n     </tr>\r\n     <tr>\r\n      <td colspan="2" {$valueStyle}>\r\n       {$event.event_title}<br />\r\n       {$event.event_start_date|date_format:"%A"} {$event.event_start_date|crmDate}{if $event.event_end_date}-{if $event.event_end_date|date_format:"%Y%m%d" == $event.event_start_date|date_format:"%Y%m%d"}{$event.event_end_date|crmDate:0:1}{else}{$event.event_end_date|date_format:"%A"} {$event.event_end_date|crmDate}{/if}{/if}\r\n      </td>\r\n     </tr>\r\n\r\n\r\n     {if $conference_sessions}\r\n      <tr>\r\n       <td colspan="2" {$labelStyle}>\r\n	{ts}Your schedule:{/ts}\r\n       </td>\r\n      </tr>\r\n      <tr>\r\n       <td colspan="2" {$valueStyle}>\r\n	{assign var=''group_by_day'' value=''NA''}\r\n	{foreach from=$conference_sessions item=session}\r\n	 {if $session.start_date|date_format:"%Y/%m/%d" != $group_by_day|date_format:"%Y/%m/%d"}\r\n	  {assign var=''group_by_day'' value=$session.start_date}\r\n          <em>{$group_by_day|date_format:"%m/%d/%Y"}</em><br />\r\n	 {/if}\r\n	 {$session.start_date|crmDate:0:1}{if $session.end_date}-{$session.end_date|crmDate:0:1}{/if} {$session.title}<br />\r\n	 {if $session.location}&nbsp;&nbsp;&nbsp;&nbsp;{$session.location}<br />{/if}\r\n	{/foreach}\r\n       </td>\r\n      </tr>\r\n     {/if}\r\n\r\n     {if $event.participant_role neq ''Attendee'' and $defaultRole}\r\n      <tr>\r\n       <td {$labelStyle}>\r\n        {ts}Participant Role{/ts}\r\n       </td>\r\n       <td {$valueStyle}>\r\n        {$event.participant_role}\r\n       </td>\r\n      </tr>\r\n     {/if}\r\n\r\n     {if $isShowLocation}\r\n      <tr>\r\n       <td colspan="2" {$valueStyle}>\r\n        {if $location.address.1.name}\r\n         {$location.address.1.name}<br />\r\n        {/if}\r\n        {if $location.address.1.street_address}\r\n         {$location.address.1.street_address}<br />\r\n        {/if}\r\n        {if $location.address.1.supplemental_address_1}\r\n         {$location.address.1.supplemental_address_1}<br />\r\n        {/if}\r\n        {if $location.address.1.supplemental_address_2}\r\n         {$location.address.1.supplemental_address_2}<br />\r\n        {/if}\r\n        {if $location.address.1.city}\r\n         {$location.address.1.city}, {$location.address.1.state_province} {$location.address.1.postal_code}{if $location.address.1.postal_code_suffix} - {$location.address.1.postal_code_suffix}{/if}<br />\r\n        {/if}\r\n       </td>\r\n      </tr>\r\n     {/if}\r\n\r\n     {if $location.phone.1.phone || $location.email.1.email}\r\n      <tr>\r\n       <td colspan="2" {$labelStyle}>\r\n        {ts}Event Contacts:{/ts}\r\n       </td>\r\n      </tr>\r\n      {foreach from=$location.phone item=phone}\r\n       {if $phone.phone}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {if $phone.phone_type}\r\n           {$phone.phone_type_display}\r\n          {else}\r\n           {ts}Phone{/ts}\r\n          {/if}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$phone.phone} {if $phone.phone_ext}&nbsp;{ts}ext.{/ts} {$phone.phone_ext}{/if}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n      {/foreach}\r\n      {foreach from=$location.email item=eventEmail}\r\n       {if $eventEmail.email}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {ts}Email{/ts}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$eventEmail.email}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n      {/foreach}\r\n     {/if}\r\n     <tr>\r\n      <td colspan="2" {$valueStyle}>\r\n      </td>\r\n     </tr>\r\n    {if $event.is_share}\r\n        <tr>\r\n            <td colspan="2" {$valueStyle}>\r\n                {capture assign=eventUrl}{crmURL p=''civicrm/event/info'' q="id=`$event.id`&reset=1" a=true fe=1 h=1}{/capture}\r\n                {include file="CRM/common/SocialNetwork.tpl" emailMode=true url=$eventUrl title=$event.title pageURL=$eventUrl}\r\n            </td>\r\n        </tr>\r\n    {/if}\r\n    {if $payer.name}\r\n     <tr>\r\n       <th {$headerStyle}>\r\n         {ts}You were registered by:{/ts}\r\n       </th>\r\n     </tr>\r\n     <tr>\r\n       <td colspan="2" {$valueStyle}>\r\n	      {$payer.name}\r\n       </td>\r\n     </tr>\r\n    {/if}\r\n    {if $event.is_monetary}\r\n\r\n      <tr>\r\n       <th {$headerStyle}>\r\n        {$event.fee_label}\r\n       </th>\r\n      </tr>\r\n\r\n      {if $lineItem}\r\n       {foreach from=$lineItem item=value key=priceset}\r\n        {if $value neq ''skip''}\r\n         {if $isPrimary}\r\n          {if $lineItem|@count GT 1} {* Header for multi participant registration cases. *}\r\n           <tr>\r\n            <td colspan="2" {$labelStyle}>\r\n             {ts 1=$priceset+1}Participant %1{/ts} {$part.$priceset.info}\r\n            </td>\r\n           </tr>\r\n          {/if}\r\n         {/if}\r\n         <tr>\r\n          <td colspan="2" {$valueStyle}>\r\n           <table> {* FIXME: style this table so that it looks like the text version (justification, etc.) *}\r\n            <tr>\r\n             <th>{ts}Item{/ts}</th>\r\n             <th>{ts}Qty{/ts}</th>\r\n             <th>{ts}Each{/ts}</th>\r\n             <th>{ts}Total{/ts}</th>\r\n	     {if  $pricesetFieldsCount }<th>{ts}Total Participants{/ts}</th>{/if}\r\n            </tr>\r\n            {foreach from=$value item=line}\r\n             <tr>\r\n              <td>\r\n              {if $line.html_type eq ''Text''}{$line.label}{else}{$line.field_title} - {$line.label}{/if} {if $line.description}<div>{$line.description|truncate:30:"..."}</div>{/if}\r\n              </td>\r\n              <td>\r\n               {$line.qty}\r\n              </td>\r\n              <td>\r\n               {$line.unit_price|crmMoney:$currency}\r\n              </td>\r\n              <td>\r\n               {$line.line_total|crmMoney:$currency}\r\n              </td>\r\n	      {if $pricesetFieldsCount }<td>{$line.participant_count}</td> {/if}\r\n             </tr>\r\n            {/foreach}\r\n           </table>\r\n          </td>\r\n         </tr>\r\n        {/if}\r\n       {/foreach}\r\n      {/if}\r\n\r\n      {if $amounts && !$lineItem}\r\n       {foreach from=$amounts item=amnt key=level}\r\n        <tr>\r\n         <td colspan="2" {$valueStyle}>\r\n          {$amnt.amount|crmMoney:$currency} {$amnt.label}\r\n         </td>\r\n        </tr>\r\n       {/foreach}\r\n      {/if}\r\n\r\n      {if $isPrimary}\r\n       <tr>\r\n        <td {$labelStyle}>\r\n         {ts}Total Amount{/ts}\r\n        </td>  \r\n        <td {$valueStyle}>\r\n         {$totalAmount|crmMoney:$currency} {if $hookDiscount.message}({$hookDiscount.message}){/if}\r\n        </td>\r\n       </tr>\r\n       {if $pricesetFieldsCount }\r\n     <tr>\r\n       <td {$labelStyle}> \r\n      {ts}Total Participants{/ts}</td>   \r\n      <td {$valueStyle}>\r\n      {assign var="count" value= 0}	 \r\n      {foreach from=$lineItem item=pcount}\r\n      {assign var="lineItemCount" value=0}\r\n      {if $pcount neq ''skip''}\r\n        {foreach from=$pcount item=p_count}\r\n        {assign var="lineItemCount" value=$lineItemCount+$p_count.participant_count}\r\n        {/foreach}\r\n      {if $lineItemCount < 1 }\r\n        {assign var="lineItemCount" value=1}\r\n      {/if}	\r\n      {assign var="count" value=$count+$lineItemCount}\r\n      {/if}\r\n      {/foreach}\r\n     {$count}\r\n     </td> </tr>\r\n      {/if}\r\n\r\n       {if $register_date}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {ts}Registration Date{/ts}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$register_date|crmDate}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n\r\n       {if $receive_date}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {ts}Transaction Date{/ts}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$receive_date|crmDate}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n\r\n       {if $contributionTypeName}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {ts}Financial Type{/ts}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$contributionTypeName}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n\r\n       {if $trxn_id}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {ts}Transaction #{/ts}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$trxn_id}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n\r\n       {if $paidBy}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {ts}Paid By{/ts}\r\n         </td>\r\n         <td {$valueStyle}>\r\n         {$paidBy}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n\r\n       {if $checkNumber}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {ts}Check Number{/ts}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$checkNumber}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n\r\n       {if $contributeMode ne ''notify'' and !$isAmountzero and !$is_pay_later and !$isOnWaitlist and !$isRequireApproval}\r\n        <tr>\r\n         <th {$headerStyle}>\r\n          {ts}Billing Name and Address{/ts}\r\n         </th>\r\n        </tr>\r\n        <tr>\r\n         <td colspan="2" {$valueStyle}>\r\n          {$billingName}<br />\r\n          {$address|nl2br}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n\r\n       {if $contributeMode eq ''direct'' and !$isAmountzero and !$is_pay_later and !$isOnWaitlist and !$isRequireApproval}\r\n        <tr>\r\n         <th {$headerStyle}>\r\n          {ts}Credit Card Information{/ts}\r\n         </th>\r\n        </tr>\r\n        <tr>\r\n         <td colspan="2" {$valueStyle}>\r\n          {$credit_card_type}<br />\r\n          {$credit_card_number}<br />\r\n          {ts}Expires{/ts}: {$credit_card_exp_date|truncate:7:''''|crmDate}\r\n         </td>\r\n        </tr>\r\n       {/if}\r\n\r\n      {/if}\r\n\r\n     {/if} {* End of conditional section for Paid events *}\r\n\r\n\r\n{if $customPre}\r\n{foreach from=$customPre item=customPr key=i}\r\n   <tr> <th {$headerStyle}>{$customPre_grouptitle.$i}</th></tr>\r\n   {foreach from=$customPr item=customValue key=customName}\r\n   {if ( $trackingFields and ! in_array( $customName, $trackingFields ) ) or ! $trackingFields}\r\n     <tr>\r\n         <td {$labelStyle}>{$customName}</td>\r\n         <td {$valueStyle}>{$customValue}</td>\r\n     </tr>\r\n   {/if}\r\n   {/foreach}\r\n{/foreach}\r\n{/if}\r\n\r\n{if $customPost}\r\n{foreach from=$customPost item=customPos key=j}\r\n   <tr> <th {$headerStyle}>{$customPost_grouptitle.$j}</th></tr>\r\n   {foreach from=$customPos item=customValue key=customName}\r\n   {if ( $trackingFields and ! in_array( $customName, $trackingFields ) ) or ! $trackingFields}\r\n     <tr>\r\n         <td {$labelStyle}>{$customName}</td>\r\n         <td {$valueStyle}>{$customValue}</td>\r\n     </tr>\r\n{/if}\r\n{/foreach}\r\n{/foreach}\r\n{/if}\r\n\r\n{if $customProfile}\r\n{foreach from=$customProfile.profile item=eachParticipant key=participantID}\r\n     <tr><th {$headerStyle}>{ts 1=$participantID+2}Participant %1{/ts} </th></tr>\r\n     {foreach from=$eachParticipant item=eachProfile key=pid}\r\n     <tr><th {$headerStyle}>{$customProfile.title.$pid}</th></tr>\r\n     {foreach from=$eachProfile item=val key=field}\r\n     <tr>{foreach from=$val item=v key=f}\r\n         <td {$labelStyle}>{$field}</td> \r\n         <td {$valueStyle}>{$v}</td> \r\n         {/foreach}\r\n     </tr>\r\n     {/foreach}\r\n{/foreach}\r\n{/foreach}\r\n{/if}\r\n\r\n    {if $customGroup}\r\n      {foreach from=$customGroup item=value key=customName}\r\n       <tr>\r\n        <th {$headerStyle}>\r\n         {$customName}\r\n        </th>\r\n       </tr>\r\n       {foreach from=$value item=v key=n}\r\n        <tr>\r\n         <td {$labelStyle}>\r\n          {$n}\r\n         </td>\r\n         <td {$valueStyle}>\r\n          {$v}\r\n         </td>\r\n        </tr>\r\n       {/foreach}\r\n      {/foreach}\r\n     {/if}\r\n   \r\n    </table>\r\n   </td>\r\n  </tr>\r\n </table>\r\n</center>\r\n\r\n</body>\r\n</html>',
  `is_active` = 1,
  `workflow_id` = 502,
  `is_default` = 1,
  `is_reserved` = 0,
  `pdf_format_id` = NULL
WHERE `id` = 19;